from shiny.express import input, render, ui, reactive
from shinywidgets import render_plotly
import shiny

# A simple in-memory check for username and password (you can replace it with something more secure)
VALID_USERNAME = "user"
VALID_PASSWORD = "password"

# Define the UI
ui.page_opts(title="Penguins dashboard", fillable=True)

# Create a reactive value to track whether the user is logged in
login_status = reactive.Value(False)

# Login page UI
def login_page():
    return ui.page_fluid(
        ui.h2("Login"),
        ui.input_text("username", "Username", ""),
        ui.input_password("password", "Password", ""),
        ui.input_action_button("login_btn", "Login")
    )

# Main app UI (Penguins dashboard)
def main_page():
    with ui.sidebar():
        ui.input_selectize(
            "var", "Select variable",
            ["bill_length_mm", "bill_depth_mm", "flipper_length_mm", "body_mass_g", "year"]
        )
        ui.input_numeric("bins", "Number of bins", 30)

    with ui.card(full_screen=True):
        @render_plotly
        def hist():
            import plotly.express as px
            from palmerpenguins import load_penguins
            return px.histogram(load_penguins(), x=input.var(), nbins=input.bins())

# The main UI that dynamically switches between the login page and the main app
def app_ui():
    if login_status():
        return main_page()  # Show the main app if logged in
    else:
        return login_page()  # Show the login page if not logged in

# Server logic
def server(input, output, session):
    # Check login credentials when the login button is clicked
    @reactive.Effect
    @reactive.event(input.login_btn)
    def check_login():
        username = input.username()
        password = input.password()

        # Validate the credentials
        if username == VALID_USERNAME and password == VALID_PASSWORD:
            login_status.set(True)  # Set login status to True if valid
        else:
            ui.notification_show("Invalid username or password!", type="error")

# Create the Shiny app
app = shiny.App(ui=app_ui, server=server)
