from shiny import App, ui, reactive

# Define the UI layout using `page_sidebar`
app_ui = ui.page_sidebar(
    ui.input_slider("num", "Choose a number", 1, 100, 50),  # Sidebar input
    ui.output_text_verbatim("value")  # Main panel output
)

# Define server logic
def server(input, output, session):
    @output
    @reactive.Calc
    def value():
        return f"You selected: {input.num()}"

# Create the Shiny app
app = App(app_ui, server)
