import pandas as pd
from shiny import App, render, ui

# Define the user interface of the app
app_ui = ui.page_fluid(
    ui.h2("ShinyLive CSV Viewer App"),
    ui.layout_sidebar(
        ui.sidebar(
            ui.input_file("file", "Choose CSV File", accept=[".csv"])
        ),
        ui.panel_main(
            ui.output_table("contents")
        ),
    )
)

# Define the server logic
def server(input, output, session):
    # Render the table when a file is uploaded
    @output
    @render.table
    def contents():
        file_info = input.file()
        if not file_info:
            return None
        # Read the CSV file into a DataFrame and return it
        df = pd.read_csv(file_info[0]["datapath"])
        return df

# Create the app object
app = App(app_ui, server)
